create PROCEDURE [dbo].[spAsGlobalLayoutBinaryPost]
	 @idfsLayout	bigint,
	 @blbChartGeneralSettings	image,
	 @blbGisLayerGeneralSettings	image,
	 @blbGisMapGeneralSettings	image,
	 @blbPivotGridSettings	image
AS
BEGIN
	update	dbo.tasglLayout	
	set		 blbChartGeneralSettings = @blbChartGeneralSettings
			,blbGisLayerGeneralSettings= @blbGisLayerGeneralSettings
			,blbGisMapGeneralSettings = @blbGisMapGeneralSettings
			,blbPivotGridSettings = @blbPivotGridSettings
	where	 idfsLayout = @idfsLayout
END






